INSERT INTO item_text VALUES(1,'de','Trank','Stellt einen kleinen Teil der Gesundheit wieder her.');
INSERT INTO item_text VALUES(2,'de','Mega-Trank','Stellt einen großen Teil der Gesundheit wieder her.');
INSERT INTO item_text VALUES(3,'de','Maxi-Trank','Stellt Gesundheit vollständig wieder her und maximiert den Gesundheitsbalken.');
INSERT INTO item_text VALUES(4,'de','Uralter Trank','Stellt Gesundheit sowie Ausdauer wieder her und maximiert deren Balken.');
INSERT INTO item_text VALUES(5,'de','Gegengift','Der Name sagt alles: Dieses Objekt ist ein Gegengift.');
INSERT INTO item_text VALUES(6,'de','Kräutermedizin','Entfernt alle Giftstoffe und stellt Gesundheit geringfügig wieder her.');
INSERT INTO item_text VALUES(7,'de','Nullbeere','Eine geheimnisvolle Beere, die verschiedene Pestarten heilt.');
INSERT INTO item_text VALUES(8,'de','Energy-Drink','Ein von der Gilde geprüftes Getränk, das die Ausdauer stärkt. Es hilft außerdem gegen Müdigkeit!');
INSERT INTO item_text VALUES(9,'de','Ration','Essen, das etwas Ausdauer wiederherstellt.');
INSERT INTO item_text VALUES(10,'de','Blutiges Steak','Ermöglicht eine mittlere Steigerung der Ausdauer. Das Fleisch ist noch rot in der Mitte.');
INSERT INTO item_text VALUES(11,'de','Gebratenes Steak','Ermöglicht eine mittlere und dauerhaftere Steigerung der Ausdauer. Ein gut zubereitetes Stück Fleisch.');
INSERT INTO item_text VALUES(12,'de','Verkohltes Fleisch','Ermöglicht eine geringe Steigerung der Ausdauer. Ein wenig bitter, kann aber schnell gegessen werden.');
INSERT INTO item_text VALUES(13,'de','Kaltgetränk','Schützt dich vorübergehend vor extremer Hitze.');
INSERT INTO item_text VALUES(1059,'de','Heißgetränk','Schützt vorübergehend vor extremer Kälte.');
INSERT INTO item_text VALUES(14,'de','Nährstoffe','Erhöht die maximale Gesundheitsstufe sehr geringfügig.');
INSERT INTO item_text VALUES(15,'de','Mega-Nährstoffe','Erhöht die maximale Gesundheitsstufe geringfügig.');
INSERT INTO item_text VALUES(16,'de','Immunisierer','Verbessert deine natürlichen Heilkräfte.');
INSERT INTO item_text VALUES(17,'de','Astera-Jerky','Getrocknetes Fleisch aus Astera. Stärkt die natürlichen Heilungskräfte und stoppt Blutungen.');
INSERT INTO item_text VALUES(18,'de','Sprintsaft','Verringert Ermüdungserscheinungen. Reduziert die Ausdauerabnahme kurzzeitig.');
INSERT INTO item_text VALUES(19,'de','Machtsamen','Steigert vorübergehend deine Angriffskraft durch besseren Energiefluss.');
INSERT INTO item_text VALUES(20,'de','Dämonenmittel','Verbessert deinen Angriff und erfüllt dich mit – na was wohl? Dämonenkraft!');
INSERT INTO item_text VALUES(21,'de','Mega-Dämonenmittel','Verbessert deine Angriffskraft noch mehr als das Dämonenmittel.');
INSERT INTO item_text VALUES(22,'de','Machtpille','Verleiht dir vorübergehend die Kraft eines mächtigen Gottes. Sehr wirksam!');
INSERT INTO item_text VALUES(23,'de','Diamantsamen','Steigert durch die Einnahme vorübergehend die Verteidigung, indem es zu Gewebeverhärtungen kommt.');
INSERT INTO item_text VALUES(24,'de','Panzerhaut','Verbessert die Verteidigung, indem sie deine Haut hart wie Stein werden lässt.');
INSERT INTO item_text VALUES(25,'de','Mega-Panzerhaut','Verbessert deine Verteidigung noch mehr als die normale Panzerhaut.');
INSERT INTO item_text VALUES(26,'de','Diamantpille','Eine Pille, die deine Haut vorübergehend diamanthart macht. Sehr wirkungsvoll!');
INSERT INTO item_text VALUES(27,'de','Lebenspulver','Erzeugt eine heilende Wolke in der Umgebung des Benutzers.');
INSERT INTO item_text VALUES(1060,'de','Lebensstaub','Erzeugt eine Wolke mit stark heilende Eigenschaften um den Benutzer.');
INSERT INTO item_text VALUES(28,'de','Kräuterpulver','Ein mysteriöses Pulver, das eine Wolke in der Umgebung des Benutzers erzeugt, die Gesundheit heilt und Gift neutralisiert.');
INSERT INTO item_text VALUES(29,'de','Dämonenpulver','Ein mysteriöses Pulver, das eine Wolke in der Umgebung des Benutzers erzeugt, die kurzzeitig Angriffe verbessert.');
INSERT INTO item_text VALUES(30,'de','Harthülsenpulver','Ein mysteriöses Pulver, das eine Wolke in der Umgebung des Benutzers erzeugt, die kurzzeitig die Verteidigung verbessert.');
INSERT INTO item_text VALUES(31,'de','Honig','Sehr nahrhafter Honig. Kombiniere ihn mit einem Trank, um einen Mega-Trank herzustellen.');
INSERT INTO item_text VALUES(32,'de','Kraut','Eine Hauptzutat von Tränken.');
INSERT INTO item_text VALUES(33,'de','Gegengiftkraut','Eine Hauptzutat von Gegengiften.');
INSERT INTO item_text VALUES(34,'de','Feuerkraut','Eine erstaunliche entflammbare Pflanze, die zur Herstellung von Sprengstoffen verwendet wird.');
INSERT INTO item_text VALUES(35,'de','Flussfarn','Die Blätter dieser Pflanze speichern viel Wasser und sind eine Komponente für Wasser-Munition.');
INSERT INTO item_text VALUES(36,'de','Schneekraut','Eine Pflanze, die eiskalte Luft verströmt und zur Herstellung von Frost-Munition verwendet wird.');
INSERT INTO item_text VALUES(37,'de','Schlafkraut','Eine Pflanze, die einschläfernde Substanzen enthält und zur Herstellung von Beruhigungsmitteln verwendet wird.');
INSERT INTO item_text VALUES(38,'de','Efeu','Eine leichte und extrem robuste Pflanze. Sie wird zur Herstellung von Netzen für Fallgruben verwendet.');
INSERT INTO item_text VALUES(1061,'de','Pfefferschote','Eine Pflanze für die Herstellung von Heißgetränken.');
INSERT INTO item_text VALUES(1062,'de','Dämmergrasknospe','Eine Pflanze mit Heilwirkung. Mit einem Götterkäfer kombiniert entfaltet sie ihr volles Potential.');
INSERT INTO item_text VALUES(39,'de','Rauchnuss','Eine Nuss, die dichten Rauch erzeugt. In Kombination mit anderen Objekten erzeugt sie verschiedene Raucharten.');
INSERT INTO item_text VALUES(40,'de','Drachentod-Beere','Eine geheimnisvolle Beere, die zur Herstellung von Drachen-Munition verwendet wird.');
INSERT INTO item_text VALUES(41,'de','Blaupilz','Ein Pilz mit kräftigenden Eigenschaften, der zur Herstellung von erholenden Objekten genutzt wird.');
INSERT INTO item_text VALUES(42,'de','Mandragora','Ein gefährlicher Pilz, der angeblich Lebenskraft entzieht. Nötig zur Herstellung von Maxi-Tränken.');
INSERT INTO item_text VALUES(43,'de','Nitropilz','Ein schwer zu findender Pilz, der enorme Hitze erzeugen kann; nötig zur Herstellung von Schießpulver.');
INSERT INTO item_text VALUES(44,'de','Teufelspest','Ein seltener, extrem heißer Pilz. In Kombination mit einer Fassbombe erzeugt er viel stärkere Explosionen.');
INSERT INTO item_text VALUES(45,'de','Lähmpilz','Ein Pilz, der Lähmung herbeiführt. Er wird auch zur Herstellung von Beruhigungsbomben verwendet.');
INSERT INTO item_text VALUES(46,'de','Giftpilz','Ein giftiger Pilz, der als Toxin für Messer verwendet werden kann.');
INSERT INTO item_text VALUES(47,'de','Reizpilz','Ein Pilz mit einem seltsamen Geruch. Essbar? Möglicherweise... Nötig zur Herstellung von Teleportern.');
INSERT INTO item_text VALUES(48,'de','Frostpilz','Dieser Pilz ist eine Hauptzutat für Kaltgetränke.');
INSERT INTO item_text VALUES(49,'de','Bitterkäfer','Ein scharf schmeckender Käfer mit heilenden Kräften – eine Zutat für viele Medikamente.');
INSERT INTO item_text VALUES(50,'de','Blitzkäfer','Ein Insekt, das beim Sterben einen starken Blitz erzeugt. Nötig für die Herstellung von Blitzkapseln.');
INSERT INTO item_text VALUES(51,'de','Götterkäfer','Ein Insekt, das tausend Jahre lebt. Es erhält durch Kombination mit Objekten deren Eigenschaften.');
INSERT INTO item_text VALUES(52,'de','Donnerkäfer','Ein Insekt, das bei Schlägen Strom abgibt. Nötig zur Herstellung von Schockfallen und Donner-Munition.');
INSERT INTO item_text VALUES(53,'de','Köderkäfer','Ein als Köder verwendetes Insekt. Fische lieben den Geschmack und können kaum genug davon bekommen.');
INSERT INTO item_text VALUES(54,'de','Kelbi-Horn','Nicht zur Herstellung von Ausrüstung geeignet, aber dennoch integraler Bestandteil vieler Objekte.');
INSERT INTO item_text VALUES(55,'de','Sprint-Extrakt','Mysteriöse Substanz von bestimmten Monstern. Erhöht die Ausdauer.');
INSERT INTO item_text VALUES(56,'de','Nährendes Extrakt','Kann von einigen großen Monstern erhalten werden und verstärkt die Wirkung bestimmter Heilmittel.');
INSERT INTO item_text VALUES(57,'de','Kreischbeutel','Ein Monsterorgan, das beim Aufbrechen ein ohrenbetäubendes Geräusch ausstößt. Zur Herstellung von Schreikapseln.');
INSERT INTO item_text VALUES(58,'de','Beschleuniger','Erhöht die Wirkung anderer Materialien. Kann nicht allein benutzt werden.');
INSERT INTO item_text VALUES(59,'de','Beruhigungsbombe','Handgranate zum Fangen von Monstern in Fallen. Kann auf den Boden geschmettert werden.');
INSERT INTO item_text VALUES(60,'de','Blitzkapsel','Schleudermunition, die beim Aufschlag aufblitzt. Eignet sich zum Blenden von Monstern bei Treffern unter der Schnauze.');
INSERT INTO item_text VALUES(61,'de','Schreikapsel','Schleudermunition. Sie gibt einen sehr schrillen Ton von sich, der empfindliche Monster ablenkt.');
INSERT INTO item_text VALUES(62,'de','Dungkapsel','Schleudermunition, die beim Aufschlag einen starken Geruch verströmt – manche Monster fliehen davor.');
INSERT INTO item_text VALUES(63,'de','Rauchbombe','Erzeugt eine große Rauchwolke wo immer sie auch landet.');
INSERT INTO item_text VALUES(64,'de','Giftrauchbombe','Verströmt eine giftige Nebelwolke und wird im Haushalt gerne gegen Ungeziefer eingesetzt.');
INSERT INTO item_text VALUES(65,'de','Teleporter','Rufe einen Flugdrachen und reise ins nächstgelegene Lager – auch während eines Kampfes.');
INSERT INTO item_text VALUES(1339,'de','Banishing Ball',NULL);
INSERT INTO item_text VALUES(66,'de','Rohes Fleisch','Fleisch eines Monsters. Kann zubereitet, kombiniert oder für Fallen verwendet werden.');
INSERT INTO item_text VALUES(67,'de','Giftfleisch','Rohes Fleisch, das bei Verzehr zu ernsten Vergiftungen führt. Perfekt als Köder für Fallen geeignet.');
INSERT INTO item_text VALUES(68,'de','Faules Fleisch','Rohes Fleisch, das bei Verzehr lähmt – und dadurch einen tollen Köder für Fallen abgibt.');
INSERT INTO item_text VALUES(69,'de','Rauschfleisch','Rohes Fleisch, das bei Verzehr zu starker Müdigkeit führt. Perfekt als Köder für Fallen geeignet.');
INSERT INTO item_text VALUES(70,'de','Schießpulver','Eine gefährliche Substanz, die bei Erschütterung oder Erhitzung explodiert.');
INSERT INTO item_text VALUES(71,'de','Kleines Fass','Ein kleines, leeres Fass.');
INSERT INTO item_text VALUES(72,'de','Fassbombe','Eine kleine Zeitbombe.');
INSERT INTO item_text VALUES(73,'de','Abprallbombe','Hebt ab, wenn sie scharf gemacht wurde. Diese modifizierte Bombe erfordert geübte Hände.');
INSERT INTO item_text VALUES(74,'de','Mega-Abprallbombe','Eine verbesserte Version der Abprallbombe. Ihr Einsatz erfordert viel Geschick.');
INSERT INTO item_text VALUES(75,'de','Großes Fass','Ein großes, leeres Fass. Kombiniere es mit Schießpulver, um es in eine Bombe zu verwandeln.');
INSERT INTO item_text VALUES(76,'de','Große Fassbombe','Eine starke Bombe, die durch äußeren physischen Kontakt ausgelöst wird.');
INSERT INTO item_text VALUES(77,'de','Mega-Fassbombe','Eine verbesserte Große Fassbombe, die sehr wirksam gegen große Monster ist.');
INSERT INTO item_text VALUES(78,'de','Spinnennetz','Ein sehr klebriges Netz. Es wird benutzt, um Fallgruben herzustellen.');
INSERT INTO item_text VALUES(79,'de','Netz','Ein starkes Netz, das recht viel Gewicht standhalten kann. Perfekt für Fallgruben.');
INSERT INTO item_text VALUES(80,'de','Fallengerät','Ein essentielles Hilfsmittel, um Fallgruben und andere Fallen zu installieren.');
INSERT INTO item_text VALUES(81,'de','Fallgrube','Zum Fangen bestimmter Monster. Schnappt durch ein schweres Gewicht zu.');
INSERT INTO item_text VALUES(82,'de','Schockfalle','Die Schockfalle macht Ziele bewegungsunfähig und erlaubt es, Monster zu fangen.');
INSERT INTO item_text VALUES(83,'de','Gerollter Dung','Eine Kugel aus konzentriertem Monsterdung. Zur Herstellung von Dungkapseln für die Schleuder.');
INSERT INTO item_text VALUES(84,'de','Dung','Stinkt furchtbar. Wird zur Herstellung von Dungkapseln für die Schleuder verwendet.');
INSERT INTO item_text VALUES(85,'de','Arowana-Köder','Exemplare der Arowana-Familie bevorzugen diesen Köder.');
INSERT INTO item_text VALUES(86,'de','Schießpulverfisch-Köder','Exemplare der Schießpulverfisch-Familie bevorzugen diesen Köder.');
INSERT INTO item_text VALUES(87,'de','Goldenfisch-Köder','Exemplare der Goldenfisch-Familie bevorzugen diesen Köder.');
INSERT INTO item_text VALUES(88,'de','Bumerang','Eine ungewöhnlich geformte, fremdländische Waffe, die zu ihrem Werfer zurückkehrt.');
INSERT INTO item_text VALUES(89,'de','Fernglas','Mit diesem feinen Objekt kannst du weiter sehen und Dinge aus der Ferne beobachten.');
INSERT INTO item_text VALUES(90,'de','Kraftamulett','Ein Amulett, das deine Angriffe verstärkt und dir Kraft eines Dämons verleiht.');
INSERT INTO item_text VALUES(91,'de','Kraftkralle','Ein Talisman, der Angriffe verbessert. Dieser Effekt wird durch enthaltene Drachenteile verstärkt.');
INSERT INTO item_text VALUES(92,'de','Panzeramulett','Ein Amulett, das die Verteidigung stärkt. Wenn du es trägst, macht es deine Haut so hart wie Stein.');
INSERT INTO item_text VALUES(93,'de','Panzerkralle','Ein Talisman, der die Verteidigung erhöht. Dieser Effekt wird durch enthaltene Drachenteile verstärkt.');
INSERT INTO item_text VALUES(94,'de','Nadelbeere','Eine Nuss mit nadelartigen Stacheln, die zur Herstellung von Schrotmunition verwendet wird.');
INSERT INTO item_text VALUES(95,'de','Sprengnuss','Eine Nuss, die zur Herstellung von Detonation-Munition verwendet wird.');
INSERT INTO item_text VALUES(96,'de','Drachenschlag-Nuss','Eine Nuss, die beim Aufschlag explodiert und zur Herstellung von Wyvernmunition verwendet wird.');
INSERT INTO item_text VALUES(97,'de','Schlitzbeere','Eine Beere, die zur Herstellung von Schnitt-Munition verwendet wird.');
INSERT INTO item_text VALUES(98,'de','Schnallenbeere','Eine Nuss mit spiralförmigen Kerben, die zur Herstellung von panzerbrechender Munition verwendet wird.');
INSERT INTO item_text VALUES(99,'de','Bombeere','Eine Nuss, die beim Aufschlag aufplatzt und zur Herstellung von Streuung-Munition verwendet wird.');
INSERT INTO item_text VALUES(100,'de','Feuernuss','Wird zur Herstellung von Schießpulver verwendet, das die Feuerkraft von Munition erhöht.');
INSERT INTO item_text VALUES(101,'de','Brandnuss','Wird zur Herstellung von Schießpulver verwendet, das die Feuerkraft von Munition stark erhöht.');
INSERT INTO item_text VALUES(102,'de','Schießpulver Stufe 2','Brennbares Pulver. Steigert in Kombination mit Stufe-1-Munition deren Stufe.');
INSERT INTO item_text VALUES(103,'de','Schießpulver Stufe 3','Leicht brennbares Pulver. Steigert in Kombination mit Stufe-1-Munition deren Stufe.');
INSERT INTO item_text VALUES(1209,'de','Blüten-Feuerwerk','Feuerwerk, für die Teilnahme am Frühlingsblüten-Fest erhalten. Bringe den Himmel und die Herzen zum Leuchten.');
INSERT INTO item_text VALUES(1210,'de','Dämmer-Feuerwerk','Feuerwerk, für die Teilnahme am Sommerdämmerung-Fest erhalten. Genieße die schwüle Sommerhitze!');
INSERT INTO item_text VALUES(1211,'de','Ernte-Feuerwerk','Feuerwerk, für die Teilnahme am Herbsternte-Fest erhalten. Tanze bis der Boden förmlich explodiert.');
INSERT INTO item_text VALUES(1212,'de','Stern-Feuerwerk','Feuerwerk, für die Teilnahme am Winterstern-Fest erhalten. Genieße die Farben und den Lärm!');
INSERT INTO item_text VALUES(1213,'de','Dankbarkeit-Feuerwerk','Feuerwerk, für die Teilnahme am Fest der Dankbarkeit erhalten. Zünde es und zeige dich so erkenntlich.');
INSERT INTO item_text VALUES(114,'de','Leere Phiole','Eine ganz normale, alte, leere Phiole. Nicht mehr und nicht weniger.');
INSERT INTO item_text VALUES(104,'de','Wetzfisch-Flosse','Eine Fischflosse, um Waffen zu schärfen. Stellt Schärfe hervorragend wieder her, allerdings weniger oft als ein Wetzstein.');
INSERT INTO item_text VALUES(105,'de','Wetzfisch-Flosse+','Eine Fischflosse, um Waffen zu schärfen. Besser als ein Wetzstein. Kann wiederverwendet werden, wenn du Glück hast.');
INSERT INTO item_text VALUES(106,'de','Sushifisch-Schuppe','Die Schuppe von einem Sushifisch. Stillt Blutungen und stellt kleine Mengen Gesundheit wieder her.');
INSERT INTO item_text VALUES(107,'de','Großer-Sushifisch-Schuppe','Die Schuppe von einem Großen Sushifisch. Behandelt Blutungen und hilft bei der Regeneration von Gesundheit.');
INSERT INTO item_text VALUES(108,'de','Schießpulverfisch-Schuppe','Die Schuppe eines Schießpulverfischs. Kombiniert mit einer Großen Fassbombe erhöht sie die Explosionskraft dramatisch.');
INSERT INTO item_text VALUES(109,'de','GroßerSchießpulverfischSchuppe','Die Schuppe eines Großen Schießpulverfischs. Kombiniert mit einer Großen Fassbombe, wird eine Mega-Fassbombe daraus.');
INSERT INTO item_text VALUES(110,'de','Berst-Arowana-Schuppe','Die Schuppe eines Berst-Arowana. Kombiniere sie mit Feuerkraut, um Schießpulver Stufe 2 herzustellen.');
INSERT INTO item_text VALUES(111,'de','Großer-Berst-Arowana-Schuppe','Die Schuppe eines Großen Berst-Arowana. Kombiniere sie mit Feuerkraut, um viel Schießpulver Stufe 2 herzustellen.');
INSERT INTO item_text VALUES(112,'de','Bomben-Arowana-Schuppe','Die Schuppe eines Bomben-Arowana. Kombiniere sie mit Feuerkraut, um Schießpulver Stufe 3 herzustellen.');
INSERT INTO item_text VALUES(113,'de','Großer-Bomben-Arowana-Schuppe','Die Schuppe eines großen Bomben-Arowana. Mit Feuerkraut wird daraus viel Schießpulver der Stufe 3.');
INSERT INTO item_text VALUES(115,'de','Eisenerz','Dieses Erz kann zu Metall geschmolzen und vielseitig verwendet werden.');
INSERT INTO item_text VALUES(116,'de','Machalit-Erz','Dieses Erz ist schwieriger als Eisenerz zu beschaffen und wird zur Herstellung von feinem Metall verwendet.');
INSERT INTO item_text VALUES(117,'de','Drachenit-Erz','Erz, das noch hochwertigeres Metall als Machalit liefert. Für die Herstellung von haltbaren Rüstungen.');
INSERT INTO item_text VALUES(118,'de','Karbalit-Erz','Ein von Förderplätzen erhaltenes Erz. Es wird noch untersucht, soll aber sehr reines Metall enthalten.');
INSERT INTO item_text VALUES(119,'de','Fusium-Erz','In der Heimat der Ältesten gefundenes Erz; ein mysteriöses Metall, das fast alle Materialien verbinden kann.');
INSERT INTO item_text VALUES(682,'de','Eltalit-Erz','Ein von Förderplätzen erhaltenes Erz. Das durchsichtige Erz ergibt Metall, das härter als Karbalit ist.');
INSERT INTO item_text VALUES(683,'de','Mischspat-Erz','Erz, oft aus dem Tal der Verwesung. Formbar, aber stark; wird häufig zum Herstellen von Legierungen genutzt.');
INSERT INTO item_text VALUES(120,'de','Erdkristall','Kristallisierte Mikroben aus der Wildturm Ödnis, die beim Schmieden als Schleifmittel wertgeschätzt werden.');
INSERT INTO item_text VALUES(121,'de','Korallenkristall','Ein Kristall, aus dem in der Schmiede Schleifmittel hergestellt werden können; aus dem Korallenhochland.');
INSERT INTO item_text VALUES(122,'de','Drachenader-Kristall','Neuentdecktes Material aus hochrangigen Gebieten. Viele denken, es läute eine neue Metallarbeit-Ära ein.');
INSERT INTO item_text VALUES(684,'de','Geistader-Kristall','In Meisterrang-Gebieten abgebaut. Nur wenig ist über diese brillant funkelnden Kristalle bekannt.');
INSERT INTO item_text VALUES(123,'de','Lichtkristall','Eine wertvolle leicht leuchtende und extrem harte Substanz, die sich auch zur Werkzeugherstellung eignet.');
INSERT INTO item_text VALUES(124,'de','Novakristall','Ein hochrangiger Lichtkristall, dessen Seltenheit ihn sehr begehrt macht; nur für feinste Metallarbeiten.');
INSERT INTO item_text VALUES(784,'de','Reiner Kristall','Ein sehr seltener Lichtkristall. Er wird wegen seiner Härte für Schmiedewerkzeug verwendet.');
INSERT INTO item_text VALUES(125,'de','Feuerzellenstein','Eine magmaartige Ablagerung, die nur von Experten bearbeitet werden kann; für erstklassige Rüstungen.');
INSERT INTO item_text VALUES(695,'de','Bathyzit-Erz','Erz aus dem Korallen-Hochland. Geformt durch jahrtausendelangen Druck auf die Knochen uralter Tiere.');
INSERT INTO item_text VALUES(696,'de','Gracium','Erz aus der Raureif-Weite. Dieses seltene und nützliche Material schimmert im Licht blassblau.');
INSERT INTO item_text VALUES(126,'de','Wasserkern-Erz','Ein wertvolles Mineral zur Herstellung von Talismanen. Der kräftige Glanz spiegelt den Ozean.');
INSERT INTO item_text VALUES(127,'de','Seelenkern-Erz','Ein wertvolles Mineral zur Herstellung von Talismanen. Gibt ein warmes, sonniges Leuchten ab.');
INSERT INTO item_text VALUES(128,'de','Traumkern-Erz','Ein wertvolles Mineral zur Herstellung von Talismanen. Der lodernde Glanz erinnert an ein Freudenfeuer.');
INSERT INTO item_text VALUES(129,'de','Drachenkern-Erz','Ein wertvolles Mineral zur Herstellung von Talismanen. Der mysteriöse Glanz gewährt dem Träger sagenhafte Weisheit.');
INSERT INTO item_text VALUES(1222,'de','Phantomkern-Erz','Ein wertvolles Mineral zur Herstellung von Talismanen. Sein Leuchten zeugt von seiner Macht.');
INSERT INTO item_text VALUES(1223,'de','Schattenkern-Erz','Ein wertvolles Mineral zur Herstellung von Talismanen. Sein Glanz zieht das Licht aus der Umgebung ab.');
INSERT INTO item_text VALUES(135,'de','Fester Knochen','Ein oft als Werkstoff benutzter Knochen. Man findet ihn im Tal der Verwesung und im Korallenhochland.');
INSERT INTO item_text VALUES(136,'de','Hochwertiger Knochen','Ein Knochen, der oft als Werkstoff benutzt wird. Leicht zu handhaben, sehr hart; für bessere Ausrüstung.');
INSERT INTO item_text VALUES(685,'de','Dicker Knochen','Seltener, als Werkstoff verwendeter Knochen. Arbeitsintensiv, bringt unvergleichliche Ergebnisse hervor.');
INSERT INTO item_text VALUES(137,'de','Alter Knochen','Ein Werkstoff aus Knochenhaufen im Uralten Wald. Schon wegen seiner Größe recht hochwertig und begehrt.');
INSERT INTO item_text VALUES(138,'de','Felsknochen','Ein Werkstoff aus Knochenhaufen in der Wildturm-Ödnis. Schon wegen seiner Größe recht hochwertig und begehrt.');
INSERT INTO item_text VALUES(139,'de','Korallenknochen','Ein Werkstoff aus Knochenhaufen im Korallenhochland. Schon wegen seiner Größe recht hochwertig und begehrt.');
INSERT INTO item_text VALUES(140,'de','Krummer Knochen','Ein Werkstoff aus Knochenhaufen im Tal der Verwesung. Schon wegen seiner Größe recht hochwertig und begehrt.');
INSERT INTO item_text VALUES(141,'de','Brutaler Knochen','Ein Werkstoff aus Knochenhaufen in der Heimat der Ältesten. Schon wegen seiner Größe recht begehrt.');
INSERT INTO item_text VALUES(143,'de','Anonymer Schädel','Ein seltenes Material aus den Knochenhaufen in der Heimat der Ältesten; wohl der Schädel eines Tieres.');
INSERT INTO item_text VALUES(697,'de','Gefrorener Knochen','Dieser Knochen aus einem Knochenhaufen der Raureif-Weite wurde durch die unerbittliche Kälte der Tundra gehärtet.');
INSERT INTO item_text VALUES(142,'de','Drachenknochen-Relikt','Ein hochwertiges Material, das in Knochenhaufen gefunden wird. Einziges Überbleibsel einer Drachenart.');
