INSERT INTO item_text VALUES(1195,'de','Verweste Schuppe','Eine Drachenschuppe, von der Zeit gezeichnet. (Kontoobjekt)');
INSERT INTO item_text VALUES(1196,'de','Leuchtende Schuppe','Eine Drachenschuppe, die trotz ihres Alters hell leuchtet. (Kontoobjekt)');
INSERT INTO item_text VALUES(1197,'de','Junge Pestwurz','Gemüse aus der Raureif-Weite. Wichtige Nährstoffquelle für die Wildtiere der Tundra. (Kontoobjekt)');
INSERT INTO item_text VALUES(1198,'de','Erlesene Pestwurz','Seltenes Gemüse aus der Raureif-Weite. Enthält genug Nährstoffe, um den Körper zu wärmen. (Kontoobjekt)');
INSERT INTO item_text VALUES(1199,'de','Millennium-Pestwurz','Seltenes Gemüse aus der Raureif-Weite. Ist nach Jahrhunderten erblüht. (Kontoobjekt)');
INSERT INTO item_text VALUES(1200,'de','Eisblüte','Eine Blume aus der Raureif-Weite, deren Blätter glasig wie Eis sind. (Kontoobjekt)');
INSERT INTO item_text VALUES(1201,'de','Mondlicht-Eisblüte','Seltene Blume aus der Raureif-Weite, die im Mondschein funkelt. (Kontoobjekt)');
INSERT INTO item_text VALUES(1202,'de','Schneegipfel-Eisblüte','Seltene Blume aus der Raureif-Weite, die angeblich nach schwersten Schneestürmen blüht. (Kontoobjekt)');
INSERT INTO item_text VALUES(1203,'de','Schneeweißchen','Seltene Blume aus der Raureif-Weite. Dieser Spross wurde vom Schnee geschützt. (Kontoobjekt)');
INSERT INTO item_text VALUES(1204,'de','Kristallblatt','Seltene Blume aus der Raureif-Weite. Wunderschön, doch vergänglich wie Schnee. (Kontoobjekt)');
INSERT INTO item_text VALUES(1205,'de','Kristalliner Knochensplitter','Ein ungewöhnliches Knochenfragment aus den geologischen Schichten der Leitlande. (Kontoobjekt)');
INSERT INTO item_text VALUES(1206,'de','Kristalliner Monsterknochen','Ein ungewöhnlicher Monsterknochen aus den Leitlanden. Leuchtet in jedem Biom anders. (Kontoobjekt)');
INSERT INTO item_text VALUES(1207,'de','KristallDrachenältesterKnochen','Ein Drachenältester-Knochen aus den Leitlanden. Älter, als Menschen es sich vorstellen können. (Kontoobjekt)');
INSERT INTO item_text VALUES(1249,'de','Große Bestienträne','Eine große, bittere, von einer Bestie vergossene Träne. (Kontoobjekt)');
INSERT INTO item_text VALUES(638,'de','Erste Hilfe','Stelle eine geringe Menge Gesundheit wieder her. (Vorratsobjekt)');
INSERT INTO item_text VALUES(639,'de','Erste Hilfe+','Stelle eine mittlere Menge Gesundheit wieder her. (Vorratsobjekt)');
INSERT INTO item_text VALUES(640,'de','Simple Ration','Stellt eine kleine Menge Ausdauer wieder her. (Vorratsobjekt)');
INSERT INTO item_text VALUES(1214,'de','Simples Lebenspulver','Erzeugt eine heilende Wolke in der Umgebung des Benutzers. (Vorratsobjekt)');
INSERT INTO item_text VALUES(1215,'de','Simpler Lebensstaub','Erzeugt eine Wolke um dich herum, die eine große Menge Gesundheit wiederherstellt. (Vorratsobjekt)');
INSERT INTO item_text VALUES(641,'de','Simpler Maxi-Trank','Stellt die volle Gesundheit wieder her und maximiert die Gesundheitsanzeige. (Vorratsobjekt)');
INSERT INTO item_text VALUES(1216,'de','Simple Große Fassbombe','Eine verstärkte Große Fassbombe, so stark wie eine Mega-Fassbombe. (Vorratsobjekt)');
INSERT INTO item_text VALUES(642,'de','Simple Schockfalle','Setzt Ziele zum leichten Fangen außer Gefecht. (Vorratsobjekt)');
INSERT INTO item_text VALUES(643,'de','Simple Fallgrube','Aktiviert eine Fallgrube zum Fangen von Monstern. (Vorratsobjekt)');
INSERT INTO item_text VALUES(1217,'de','Simples Kräuterpulver','Heilt Gift und etwas Gesundheit bei allen in der Umgebung. (Vorratsobjekt)');
INSERT INTO item_text VALUES(1218,'de','Simples Dämonenpulver','Verbessert kurzzeitig Angriffe bei allen in der Umgebung. (Vorratsobjekt)');
INSERT INTO item_text VALUES(1219,'de','Simples Harthülsenpulver','Erhöht kurzzeitig die Verteidigung bei allen in der Umgebung. (Vorratsobjekt)');
INSERT INTO item_text VALUES(644,'de','Simple Blitzkapsel','Schleudermunition, die beim Aufschlag hell leuchtet. (Vorratsobjekt)');
INSERT INTO item_text VALUES(645,'de','Simple Schreikapsel','Schleudermunition, die ein ohrenbetäubendes Geräusch ausstößt. (Vorratsobjekt)');
INSERT INTO item_text VALUES(646,'de','Simple Dungkapsel','Verströmt beim Aufschlag einen starken Geruch – manche Monster fliehen davor. (Vorratsobjekt)');
INSERT INTO item_text VALUES(647,'de','Wurfmesser','Ein Messer, das mit der Schleuder verschossen und mit Chemikalien beschichtet werden kann. (Vorratsobjekt)');
INSERT INTO item_text VALUES(648,'de','Giftmesser','Ein vergiftetes Messer, das mit der Schleuder verschossen werden kann. (Vorratsobjekt)');
INSERT INTO item_text VALUES(649,'de','Schlafmesser','Ein mit einem Beruhigungsmittel überzogenes Messer, das mit der Schleuder verschossen werden kann. (Vorratsobjekt)');
INSERT INTO item_text VALUES(650,'de','Lähmungsmesser','Ein mit Lähmungsgift überzogenes Messer, das mit der Schleuder verschossen werden kann. (Vorratsobjekt)');
INSERT INTO item_text VALUES(651,'de','Beruhigungsmesser','Ein Messer mit Beruhigungsbeschichtung, das mit der Schleuder verschossen werden kann. (Vorratsobjekt)');
INSERT INTO item_text VALUES(652,'de','Simpler Teleporter','Rufe einen Flugdrachen und reise ins nächstgelegene Lager. (Vorratsobjekt)');
INSERT INTO item_text VALUES(653,'de','Simple Beruhigungsbombe','Eine Handgranate, um in Fallen steckende Monster zu fangen. (Vorratsobjekt)');
INSERT INTO item_text VALUES(1063,'de','Grünes Kraut','Ein in den Arklay-Bergen beheimatetes grünes Kraut. Stellt Gesundheit wieder her, heilt Zombifizierung (nur auf Quests).');
INSERT INTO item_text VALUES(1064,'de','Rotes Kraut','Ein in den Arklay-Bergen beheimatetes rotes Kraut. Kann in dieser Form nicht genutzt werden (nur auf Quests).');
INSERT INTO item_text VALUES(1065,'de','Gemischte Kräuter (G+R)','Diese Mischung aus zwei Kräutern stellt Gesundheit ganz wieder her und heilt Zombifizierung (nur auf Quests).');
INSERT INTO item_text VALUES(1,'en','Potion','Restores a small amount of health.');
INSERT INTO item_text VALUES(2,'en','Mega Potion','Restores a moderate amount of health.');
INSERT INTO item_text VALUES(3,'en','Max Potion','Fully restores health and maximizes the size of your Health Gauge.');
INSERT INTO item_text VALUES(4,'en','Ancient Potion','Fully restores health and maximizes the size of your Health and Stamina Gauges.');
INSERT INTO item_text VALUES(5,'en','Antidote','A cure for poison.');
INSERT INTO item_text VALUES(6,'en','Herbal Medicine','Removes all traces of poison and restores a slight amount of health.');
INSERT INTO item_text VALUES(7,'en','Nulberry','A mysterious berry that cures various blights.');
INSERT INTO item_text VALUES(8,'en','Energy Drink','A Guild-approved beverage that boosts your stamina. Helps shake off sleep, too!');
INSERT INTO item_text VALUES(9,'en','Ration','Food that restores a bit of stamina.');
INSERT INTO item_text VALUES(10,'en','Rare Steak','Provides a moderate boost to your stamina. This one''s still red in the middle.');
INSERT INTO item_text VALUES(11,'en','Well-done Steak','Provides a moderate boost to your stamina and keeps it at max for a longer period. Meat done right!');
INSERT INTO item_text VALUES(12,'en','Burnt Meat','Provides a small boost to your stamina. Can be eaten quickly.');
INSERT INTO item_text VALUES(13,'en','Cool Drink','Provides temporary relief from extreme heat.');
INSERT INTO item_text VALUES(1059,'en','Hot Drink','Provides temporary relief from extreme cold.');
INSERT INTO item_text VALUES(14,'en','Nutrients','Very slightly increases your maximum health level.');
INSERT INTO item_text VALUES(15,'en','Mega Nutrients','Slightly increases your maximum health level.');
INSERT INTO item_text VALUES(16,'en','Immunizer','Boosts your natural ability to heal.');
INSERT INTO item_text VALUES(17,'en','Astera Jerky','Juicy, dried meat made by Astera''s chef. Boosts natural healing and cures bleeding.');
INSERT INTO item_text VALUES(18,'en','Dash Juice','Dulls one''s sense of exhaustion. Reduces stamina depletion for a time.');
INSERT INTO item_text VALUES(19,'en','Might Seed','Temporarily strengthens your attacks when ingested by improving energy flow.');
INSERT INTO item_text VALUES(20,'en','Demondrug','Boosts your attack power by filling you with—guess what?—demonic strength.');
INSERT INTO item_text VALUES(21,'en','Mega Demondrug','Boosts your attack power even more than a regular Demondrug.');
INSERT INTO item_text VALUES(22,'en','Might Pill','Temporarily endows you with the strength of a mighty god. Potent!');
INSERT INTO item_text VALUES(23,'en','Adamant Seed','Temporarily raises your defense when ingested by hardening tissue.');
INSERT INTO item_text VALUES(24,'en','Armorskin','Boosts your defense by turning your skin as hard as rock.');
INSERT INTO item_text VALUES(25,'en','Mega Armorskin','Boosts your defense even more than a regular armorskin.');
INSERT INTO item_text VALUES(26,'en','Adamant Pill','Temporarily makes your skin as hard as adamant. Potent!');
INSERT INTO item_text VALUES(27,'en','Lifepowder','Produces a healing cloud in the area around the user.');
INSERT INTO item_text VALUES(1060,'en','Dust of Life','Produces a cloud in the area around the user with strong healing properties.');
INSERT INTO item_text VALUES(28,'en','Herbal Powder','A mysterious powder that produces a cloud around the area that restores health and neutralizes poison.');
INSERT INTO item_text VALUES(29,'en','Demon Powder','A mysterious powder that produces a cloud around the area that temporarily strengthens attacks.');
INSERT INTO item_text VALUES(30,'en','Hardshell Powder','A mysterious powder that produces a cloud around the area that temporarily strengthens defense.');
INSERT INTO item_text VALUES(31,'en','Honey','Highly nutritious honey. Combine this with Potions to craft Mega Potions for maximum potency.');
INSERT INTO item_text VALUES(32,'en','Herb','A primary ingredient in potions.');
INSERT INTO item_text VALUES(33,'en','Antidote Herb','A primary ingredient in antidotes.');
INSERT INTO item_text VALUES(34,'en','Fire Herb','A wondrous, flammable plant. Useful for crafting explosives.');
INSERT INTO item_text VALUES(35,'en','Flowfern','This plant''s leaves store a great amount of water. A component for making Water Ammo.');
INSERT INTO item_text VALUES(36,'en','Snow Herb','A plant that radiates ice-cold air. Use in combinations to create freeze ammo.');
INSERT INTO item_text VALUES(37,'en','Sleep Herb','A plant containing sleeping agents. Used to craft tranquilizers.');
INSERT INTO item_text VALUES(38,'en','Ivy','A lightweight and extremely strong plant. Used to make nets for pitfall traps.');
INSERT INTO item_text VALUES(1061,'en','Hot Pepper','A plant used for making hot drinks.');
INSERT INTO item_text VALUES(1062,'en','Gloamgrass Bud','A plant with healing properties. Combine it with a Godbug to bring out its full potency.');
INSERT INTO item_text VALUES(39,'en','Smokenut','A nut that produces a copious amount of smoke. Combine with other items to create smoke with various properties.');
INSERT INTO item_text VALUES(40,'en','Dragonfell Berry','A mysterious berry, rumored for eons to be loathed by dragons. Required to craft Dragon Ammo.');
INSERT INTO item_text VALUES(41,'en','Blue Mushroom','A mushroom infused with power-enhancing effects. Used in restorative items.');
INSERT INTO item_text VALUES(42,'en','Mandragora','A dangerous fungus said to draw the life out of people. Beware. Required to craft Max Potions.');
INSERT INTO item_text VALUES(43,'en','Nitroshroom','A hard-to-find mushroom with the power to generate blazing heat. Required to craft Gunpowder.');
INSERT INTO item_text VALUES(44,'en','Devil''s Blight','A very rare fungus that pulses with extreme heat. Add to a barrel bomb for a dramatically stronger blast.');
INSERT INTO item_text VALUES(45,'en','Parashroom','A mushroom that induces paralysis. Required to craft Tranq Bombs.');
INSERT INTO item_text VALUES(46,'en','Toadstool','A poisonous fungus. Can be used as a toxin for knives.');
INSERT INTO item_text VALUES(47,'en','Exciteshroom','This one has a very strange aroma. Is it edible? Only one way to find out! Required to craft Farcasters.');
INSERT INTO item_text VALUES(48,'en','Chillshroom','This mushroom is a primary ingredient in cool drinks.');
INSERT INTO item_text VALUES(49,'en','Bitterbug','A sharp-tasting bug with innate healing abilities. Used as an ingredient in many medicines.');
INSERT INTO item_text VALUES(50,'en','Flashbug','An insect that emits a powerful flash when it dies. Required to craft Flash Pods.');
INSERT INTO item_text VALUES(51,'en','Godbug','An insect said to live a millennium. Combine it with other items to add their properties to the powder.');
INSERT INTO item_text VALUES(52,'en','Thunderbug','An insect that emits electricity when struck. Required to craft Shock Traps and Thunder Ammo.');
INSERT INTO item_text VALUES(53,'en','Baitbug','An insect used as fishing bait. A fish favorite, it practically races down the nearest gilly gullet.');
INSERT INTO item_text VALUES(54,'en','Kelbi Horn','While not practical when forging equipment, Kelbi horns are a vital component of many items.');
INSERT INTO item_text VALUES(55,'en','Dash Extract','A mysterious substance culled from certain monsters that increases endurance.');
INSERT INTO item_text VALUES(56,'en','Nourishing Extract','Can be obtained from certain large monsters. Has the ability to amplify the effects of certain medicines.');
INSERT INTO item_text VALUES(57,'en','Screamer Sac','A monster organ that emits an earsplitting sonic blast when ruptured. Used to craft screamer pods.');
INSERT INTO item_text VALUES(58,'en','Catalyst','Works with other materials to enhance their effects. Cannot be used by itself.');
INSERT INTO item_text VALUES(59,'en','Tranq Bomb','A hand grenade used to capture monsters ensnared in traps. Can be slammed against the ground.');
INSERT INTO item_text VALUES(60,'en','Flash Pod','Slinger ammo that flashes brightly on impact. Fire it right under a monster''s nose to blind it.');
INSERT INTO item_text VALUES(61,'en','Screamer Pod','Used as ammo for your slinger. Emits a harsh, high-frequency sound that can disrupt sensitive monsters.');
INSERT INTO item_text VALUES(62,'en','Dung Pod','Slinger ammo that releases a strong odor on impact. Causes certain monsters to flee.');
INSERT INTO item_text VALUES(63,'en','Smoke Bomb','Creates a large cloud of smoke wherever it lands.');
INSERT INTO item_text VALUES(64,'en','Poison Smoke Bomb','Releases a toxic cloud of mist when used. Also popular as a household bug bomb.');
INSERT INTO item_text VALUES(65,'en','Farcaster','Call for a wingdrake to transport you to the nearest camp. Can also be used during battles.');
INSERT INTO item_text VALUES(1339,'en','Banishing Ball',NULL);
INSERT INTO item_text VALUES(66,'en','Raw Meat','Meat carved from a monster. Can be cooked, combined, or used to set a trap.');
INSERT INTO item_text VALUES(67,'en','Poisoned Meat','Raw meat that poisons whatever eats it. Makes great bait for traps.');
INSERT INTO item_text VALUES(68,'en','Tinged Meat','Raw meat that paralyzes whatever eats it. Makes great bait for traps.');
INSERT INTO item_text VALUES(69,'en','Drugged Meat','Raw meat that puts whatever eats it to sleep. Makes great bait for traps.');
INSERT INTO item_text VALUES(70,'en','Gunpowder','A dangerous substance that explodes when struck or heated.');
INSERT INTO item_text VALUES(71,'en','Small Barrel','A small, empty barrel.');
INSERT INTO item_text VALUES(72,'en','Barrel Bomb','A small time bomb.');
INSERT INTO item_text VALUES(73,'en','Bounce Bomb','Launches into the air when set. This modified bomb requires skill to use.');
INSERT INTO item_text VALUES(74,'en','Mega Bounce Bomb','An improved version of the bounce bomb. Requires some skill to use.');
INSERT INTO item_text VALUES(75,'en','Large Barrel','A large, empty barrel. Combine with gunpowder to make it into a bomb.');
INSERT INTO item_text VALUES(76,'en','Large Barrel Bomb','A powerful bomb triggered by external physical impact.');
INSERT INTO item_text VALUES(77,'en','Mega Barrel Bomb','An upgraded large barrel bomb. Effective against large monsters.');
INSERT INTO item_text VALUES(78,'en','Spider Web','A very sticky web. Used to craft pitfall traps.');
INSERT INTO item_text VALUES(79,'en','Net','A sturdy net that supports a certain amount of weight without breaking. Perfect for pitfall traps.');
INSERT INTO item_text VALUES(80,'en','Trap Tool','A must-have item for putting together pitfall traps and other kinds of traps.');
INSERT INTO item_text VALUES(81,'en','Pitfall Trap','A trap for catching certain large monsters. It is tripped by a heavy weight.');
INSERT INTO item_text VALUES(82,'en','Shock Trap','A trap that immobilizes a target. Use it to capture monsters.');
INSERT INTO item_text VALUES(83,'en','Rolled-up Dung','A ball of condensed monster dung. Used to make dung pods for your slinger.');
INSERT INTO item_text VALUES(84,'en','Dung','Exudes a prodigious smell. Used to make ammo for your slinger called dung pods.');
INSERT INTO item_text VALUES(85,'en','Arowana Bait','A ball of bait favored by fish of the Arowana family.');
INSERT INTO item_text VALUES(86,'en','Gunpowderfish Bait','A ball of bait favored by fish of the Gunpowderfish family.');
INSERT INTO item_text VALUES(87,'en','Goldenfish Bait','A ball of bait favored by fish of the Goldenfish family.');
INSERT INTO item_text VALUES(88,'en','Boomerang','An unusually-shaped weapon from abroad that returns to its user when thrown.');
INSERT INTO item_text VALUES(89,'en','Binoculars','Allows you to see farther and observe from a distance.');
INSERT INTO item_text VALUES(90,'en','Powercharm','An amulet that strengthens attacks. Just carrying this gives you demonic strength.');
INSERT INTO item_text VALUES(91,'en','Powertalon','A charm that strengthens attacks. Its effects are amplified by the dragon parts it contains.');
INSERT INTO item_text VALUES(92,'en','Armorcharm','An amulet that increases defense. Just carrying this hardens your skin like stone.');
INSERT INTO item_text VALUES(93,'en','Armortalon','A charm that increases defense. Its effects are amplified by the dragon parts it contains.');
INSERT INTO item_text VALUES(94,'en','Needleberry','A nut covered in needle-like spines. Used to make spread ammo.');
INSERT INTO item_text VALUES(95,'en','Blastnut','A nut used to make sticky ammo.');
INSERT INTO item_text VALUES(96,'en','Dragonstrike Nut','A nut that explodes on impact. Used to make wyvern ammo.');
INSERT INTO item_text VALUES(97,'en','Slashberry','Used to make slicing ammo.');
INSERT INTO item_text VALUES(98,'en','Latchberry','A nut that bears a spiral groove. Used to make pierce ammo.');
INSERT INTO item_text VALUES(99,'en','Bomberry','A nut that ruptures on impact. Used to make cluster bombs.');
INSERT INTO item_text VALUES(100,'en','Flamenut','Used to make gunpowder that increases ammo firepower.');
INSERT INTO item_text VALUES(101,'en','Blazenut','Used to make gunpowder that greatly increases ammo firepower.');
INSERT INTO item_text VALUES(102,'en','Gunpowder Level 2','Combustible powder. Combine with level 1 ammo to raise its level.');
INSERT INTO item_text VALUES(103,'en','Gunpowder Level 3','Highly combustible powder. Combine with level 1 ammo to raise its level.');
INSERT INTO item_text VALUES(1209,'en','Blossom Fireworks','Fireworks you receive for attending the Spring Blossom Fest. Light up the sky and enjoy the festivities!');
INSERT INTO item_text VALUES(1210,'en','Twilight Fireworks','Fireworks you receive for attending the Summer Twilight Fest. Enjoy the summer heat with a bang!');
INSERT INTO item_text VALUES(1211,'en','Harvest Fireworks','Fireworks you receive for attending the Autumn Harvest Fest. Dance till the ground lights up in a blaze!');
INSERT INTO item_text VALUES(1212,'en','Star Fireworks','Fireworks you receive for attending the Winter Star Fest. Enjoy the festivities with a bang!');
INSERT INTO item_text VALUES(1213,'en','Appreciation Fireworks','Fireworks you receive for attending the Appreciation Fest. Show your thanks by lighting these fireworks!');
INSERT INTO item_text VALUES(114,'en','Empty Phial','A regular old empty phial. Nothing more, nothing less.');
INSERT INTO item_text VALUES(104,'en','Whetfish Fin','A fish fin used to sharpen weapons. Restores sharpness greatly, but good for fewer uses than a whetstone.');
INSERT INTO item_text VALUES(105,'en','Whetfish Fin+','A fish fin used to sharpen weapons. Works better than a whetstone, and can be reused if you''re lucky.');
INSERT INTO item_text VALUES(106,'en','Sushifish Scale','A scale from a Sushifish. Eat to restore a small amount of health. Also works to treat bleeding.');
INSERT INTO item_text VALUES(107,'en','Great Sushifish Scale','Scale from a Great Sushifish. Eat to get large health boost, a natural healing boost, and treat bleeding.');
INSERT INTO item_text VALUES(108,'en','Gunpowderfish Scale','The scales of a Gunpowderfish. Combine with a large barrel bomb to dramatically increase its firepower.');
INSERT INTO item_text VALUES(109,'en','Great Gunpowderfish Scale','The scales of a Great Gunpowderfish. Combine with a large barrel bomb to make a mega barrel bomb.');
INSERT INTO item_text VALUES(110,'en','Burst Arowana Scale','The scales of a Burst Arowana. Combine with fire herb to make a large amount of level 2 gunpowder.');
INSERT INTO item_text VALUES(111,'en','Great Burst Arowana Scale','The scales of a Great Burst Arowana. Combine with fire herb to make a vast amount of level 2 gunpowder.');
INSERT INTO item_text VALUES(112,'en','Bomb Arowana Scale','The scales of a Bomb Arowana. Combine with fire herb to make a large amount of Lv3 gunpowder.');
INSERT INTO item_text VALUES(113,'en','Great Bomb Arowana Scale','The scales of a Great Bomb Arowana. Combine with fire herb to make a vast amount of Lv3 gunpowder.');
INSERT INTO item_text VALUES(115,'en','Iron Ore','Ore that can be smelted into metal and used for many different purposes.');
INSERT INTO item_text VALUES(116,'en','Machalite Ore','Ore that''s more difficult to procure than Iron Ore. Produces fine metal.');
INSERT INTO item_text VALUES(117,'en','Dragonite Ore','An ore that yields metal superior to that of Machalite. Invaluable for making durable armor.');
INSERT INTO item_text VALUES(118,'en','Carbalite Ore','Ore obtained from mining outcrops. Still under research, but known to yield highly pure metal.');
INSERT INTO item_text VALUES(119,'en','Fucium Ore','Ore obtained from the Elder''s Recess. A mysterious metal, capable of fusing nearly any two materials together.');
INSERT INTO item_text VALUES(682,'en','Eltalite Ore','Ore obtained from mining outcrops. This transcendent ore refines into metal even stronger than carbalite.');
INSERT INTO item_text VALUES(683,'en','Meldspar Ore','Ore often found in the Rotten Vale. Malleable but strong, it is broadly used in creating alloys for crafting.');
INSERT INTO item_text VALUES(120,'en','Earth Crystal','Crystallized microbes prized as an abrasive when forging weapons. Mainly found in the Wildspire Waste.');
INSERT INTO item_text VALUES(121,'en','Coral Crystal','Crystal prized at the Smithy for use in high-grade abrasives. Mainly found in the Coral Highlands.');
INSERT INTO item_text VALUES(122,'en','Dragonvein Crystal','Mined from high rank areas. Many expect this newly-discovered crystal to usher in a new era of metalwork.');
INSERT INTO item_text VALUES(684,'en','Spiritvein Crystal','Mined from master rank areas. Little is known about these crystals save the complexity of their brilliance.');
INSERT INTO item_text VALUES(123,'en','Lightcrystal','A precious and extremely hard substance with a faint glow. Sometimes used for crafting tools.');
INSERT INTO item_text VALUES(124,'en','Novacrystal','High quality Lightcrystal. Highly coveted for its rarity, it''s used for only the finest of metalworking.');
INSERT INTO item_text VALUES(784,'en','Purecrystal','Very rare lightcrystal. Used by the forge workers to craft their tools due to its incredible hardness.');
INSERT INTO item_text VALUES(125,'en','Firecell Stone','A magma-like deposit that only trained hands can work with. Armor made from it is first-class.');
INSERT INTO item_text VALUES(695,'en','Bathycite Ore','Ore from the Coral Highlands. Formed by millennia of pressure acting upon the bones of ancient organisms.');
INSERT INTO item_text VALUES(696,'en','Gracium','Ore from the Hoarfrost Reach. When the light catches this rare and useful metal it takes on a pale blue shine.');
INSERT INTO item_text VALUES(126,'en','Aquacore Ore','A valuable mineral used to create charms. Gives off a vibrant glow that mirrors the ocean.');
INSERT INTO item_text VALUES(127,'en','Spiritcore Ore','A valuable mineral used to create charms. Gives off a warm, sunny glow.');
INSERT INTO item_text VALUES(128,'en','Dreamcore Ore','A valuable mineral used to create charms. Gives off a blazing glow reminiscent of a bonfire.');
INSERT INTO item_text VALUES(129,'en','Dragoncore Ore','A valuable mineral used to create charms. Gives off a mysterious glow that grants the holder sagely wisdom.');
INSERT INTO item_text VALUES(1222,'en','Phantomcore Ore','A valuable mineral used to create charms. Its otherworldly shimmering speaks to the power it contains.');
INSERT INTO item_text VALUES(1223,'en','Shadowcore Ore','A valuable mineral used to create charms. Its shine somehow saps the light away from its surroundings.');
INSERT INTO item_text VALUES(135,'en','Sturdy Bone','A bone used as crafting material. Can be found in bonepiles in the Coral Highlands and Rotten Vale.');
INSERT INTO item_text VALUES(136,'en','Quality Bone','A bone used as crafting material. Its ease of use and hardness allow it to be shaped into better gear.');
INSERT INTO item_text VALUES(685,'en','Thick Bone','Very rare bone used as crafting material. While laborious to work, it yields results like no other.');
INSERT INTO item_text VALUES(137,'en','Ancient Bone','A bone used as crafting material. Can be found in bonepiles in the Ancient Forest. Its size evokes a certain primal quality.');
INSERT INTO item_text VALUES(138,'en','Boulder Bone','A bone used as crafting material. Can be found in bonepiles in the Wildspire Waste. Its size evokes a certain primal quality.');
INSERT INTO item_text VALUES(139,'en','Coral Bone','A bone used as crafting material. Can be found in bonepiles in the Coral Highlands. Its size evokes a certain primal quality.');
INSERT INTO item_text VALUES(140,'en','Warped Bone','A bone used as crafting material. Can be found in bonepiles in the Rotten Vale. Its size evokes a certain primal quality.');
INSERT INTO item_text VALUES(141,'en','Brutal Bone','A bone used as crafting material. Can be found in bonepiles in the Elder''s Recess. Its size evokes a certain primal quality.');
INSERT INTO item_text VALUES(143,'en','Unknown Skull','A rare material found in bonepiles in the Elder''s Recess. It appears to be some kind of animal skull.');
INSERT INTO item_text VALUES(697,'en','Frozen Bone','A bone found in bonepiles in the Hoarfrost Reach. Tempered by years of the tundra''s unrelenting cold.');
INSERT INTO item_text VALUES(142,'en','Dragonbone Relic','A high quality material found in bone piles. The only remains left of a species of dragon now lost to time.');
